import React, { useState } from 'react';
import "./Cpassword.css";

const Cpassword = () => {
    const [formData, setFormData] = useState({
        currentPassword: '',
        newPassword: '',
        reenterNewPassword: ''
    });

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Add client-side validation if needed

        try {
            const response = await fetch('/change-password', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    // Add any additional headers if needed
                },
                body: JSON.stringify(formData),
            });

            if (response.ok) {
                console.log('Password changed successfully!');
                // You can handle success (e.g., show a success message, redirect, etc.)
            } else {
                console.error('Failed to change password');
                // You can handle errors (e.g., show an error message)
            }
        } catch (error) {
            console.error('Error:', error);
            // Handle other errors as needed
        }
    };

    return (
        <>
            <section className="section min-vh-100 d-flex flex-column align-items-center justify-content-center py-4">
                <div className="container Cpassword">
                    <div className="row justify-content-center">
                        <div className="card mb-3">
                            <div className="card-body">
                                <div className="pt-4 pb-2">
                                    <h5 className="card-title text-center pb-0 fs-4">Change Your Password</h5>
                                </div>
                                <form className="row g-3" onSubmit={handleSubmit}>
                                    <div className="col-12">
                                        <label htmlFor="currentPassword" className="form-label">Current Password</label>
                                        <input
                                            type="password"
                                            name="currentPassword"
                                            className="form-control"
                                            id="currentPassword"
                                            required
                                            value={formData.currentPassword}
                                            onChange={handleChange}
                                        />
                                    </div>
                                    <div className="col-12">
                                        <label htmlFor="newPassword" className="form-label">New Password</label>
                                        <input
                                            type="password"
                                            name="newPassword"
                                            className="form-control"
                                            id="newPassword"
                                            required
                                            value={formData.newPassword}
                                            onChange={handleChange}
                                        />
                                    </div>
                                    <div className="col-12">
                                        <label htmlFor="reenterNewPassword" className="form-label">Re-enter New Password</label>
                                        <input
                                            type="password"
                                            name="reenterNewPassword"
                                            className="form-control"
                                            id="reenterNewPassword"
                                            required
                                            value={formData.reenterNewPassword}
                                            onChange={handleChange}
                                        />
                                    </div>
                                    <div className="col-12 btndiv">
                                        <button className="btn btn-primary change" type="submit">Change</button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </>
    );
}

export default Cpassword;
