import React, { useState } from 'react';

const PizzaCard = ({ pizza }) => {
  const [quantity, setQuantity] = useState(1);
  const [selectedSize, setSelectedSize] = useState('small');

  const decrementQuantity = () => {
    if (quantity > 1) {
      setQuantity(quantity - 1);
    }
  };

  const incrementQuantity = () => {
    setQuantity(quantity + 1);
  };

  const addToCart = async () => {
    const data = {
      itemName: pizza.itemName,
      size: selectedSize,
      quantity: quantity,
    };

    // Perform your addToCart logic here
    console.log(data);
  };

  return (
    <div className="col-lg-3" key={pizza.id}>
      <div className="card">
        <img src={pizza.image} className="card-img-top" alt={pizza.name} />
        <div className="card-body">
          <h5 className="card-title">{pizza.itemName}</h5>
          <p className="card-text">{pizza.description}</p>
          <div className="row mb-3">
            <div className="col-lg-6">
              <label htmlFor="inputSize" className="form-label">
                Size
              </label>
              <select
                id="inputSize"
                className="form-select"
                value={selectedSize}
                onChange={(e) => {
                  setSelectedSize(e.target.value);
                }}
              >
                {Object.keys(pizza.sizes).map((size) => (
                  <option key={size} value={size}>
                    {size}
                  </option>
                ))}
              </select>
            </div>
            <div className="col-lg-6">
              <label htmlFor="yourPrice" className="form-label">
                Price
              </label>
              <h5 className="card-text">
                {pizza.sizes[selectedSize].price}
              </h5>
            </div>
          </div>
          <div className="row">
            <div className="col-lg-6">
              <button
                type="button"
                className="btn btn-outline-primary"
                onClick={decrementQuantity}
              >
                -
              </button>
              <input
                type="text"
                name="quantity"
                className="form-control"
                value={quantity}
                readOnly
              />
              <button
                type="button"
                className="btn btn-outline-primary"
                onClick={incrementQuantity}
              >
                +
              </button>
            </div>
            <div className="col-lg-6">
              <button className="btn btn-primary" onClick={addToCart}>
                Add to Cart
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PizzaCard;


