import React, { useEffect, useState } from 'react'

const Yourorder = () => {

    const [userOrders, setUserOrders] = useState([]);
    const [orderDates, setOrderDates] = useState([]);

    const callOrderPage = async () => {
        try {
            const response = await fetch("/getuserorder", {
                method: "GET",
                headers: {
                    Accept: "application/json",
                    "Content-Type": "application/json"
                },
                credentials: "include"
            });

            const res = await response.json();
            setUserOrders(res.userOrderDetails);
            setOrderDates(res.orderDates);
            console.log(res);

            if (!response.ok) {
                const error = new Error(res.error);
                throw error;
            }
        } catch (err) {
            console.log(err);
        }
    }

    useEffect(() => {
        callOrderPage();
    }, []);


    return (
        <>
            <section className="section" id="my-orders">
                <div className="section-header">
                    <p>My Orders</p>
                </div>
                <div className="container">
                    <h4 style={{ "text-align": "center" }}>Date: {orderDates}</h4>
                    <table className="table table-bordered">
                        <thead>
                            <tr>
                                <th>Sr. No.</th>
                                <th>Item Name</th>
                                <th>Quantity</th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                                <td>1</td>
                                <td>Pizza</td>
                                <td>4</td>

                            </tr>
                        </tbody>
                    </table>

                </div>
            </section>
        </>
    )
}

export default Yourorder
