import React, { useState, useEffect } from 'react'
import { DataTable } from 'simple-datatables';
import './Orderrecord.css'
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify'; // Import toast from react-toastify
import 'react-toastify/dist/ReactToastify.css'; // Import the CSS for styling


const Addpizza = () => {

    const [currentPage, setCurrentPage] = useState(1);
    // const [itemsPerPage] = useState(); // Number of items to display per page (changed to 10)
    const [items, setItems] = useState([]);
    const [updateMode, setUpdateMode] = useState(false);
    const [itemIdToUpdate, setItemIdToUpdate] = useState('');
    const [item, setItem] = useState({
        userID: "",
        shopID: "",
        orderDate: "",
        totalAmount: "",
        orderStatus: "",
        paymentStatus: ""
    });

    const navigate = useNavigate();
   

    useEffect(() => {
    

        fetch("/getorder", {
            method: "GET",
            headers: {
                Accept: "application/json",
            },
        })
            .then((response) => response.json())
            .then((data) => {
                setItems(data);
            })
            .catch((error) => {
                console.error("Error fetching items:", error);
            });
    }, []);


    // Function to handle form input changes
    const handleInputs = (e) => {
        const { name, value } = e.target;
        setItem({ ...item, [name]: value });
    };

    // Function to handle price input changes for different sizes
    const handlePriceInputs = (e, size) => {
        const { name, value } = e.target;
        setItem({
            ...item,
            sizes: {
                ...item.sizes,
                [size]: {
                    ...item.sizes[size],
                    [name]: value
                }
            }
        });
    };

    

    

    

    // Function to handle form submission for adding a new item
    const postData = async (e) => {
        e.preventDefault();

        const { itemName, description, sizes, image } = item;

        const response = await fetch("/addItem", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                Accept: "application/json",
            },
            body: JSON.stringify({
                itemName,
                description,
                sizes,
                image
            }),
        });

        const res = await response.json();

        if (response.status === 201 || res.message === 'Item added successfully') {
            console.log("Item added successfully");
            toast.success("Item added successfully");

            setItem({
                itemName: "",
                description: "",
                sizes: {
                    small: { price: "" },
                    medium: { price: "" },
                    large: { price: "" }
                },
                image: ""
            });
        } else {
            console.log("Failed to add item");
            toast.error("Failed to add item");
        }
    };


    // Megh
    useEffect(() => {
        const datatables = document.querySelectorAll('.datatable');

        datatables.forEach(datatable => {
            new DataTable(datatable);
        });
    }, []);
    return (
        <>
            <section className="section register min-vh-100 d-flex flex-column align-items-center  py-4" id="register" >
                <div className="container">
                    <div className="row ">
                        <div className="col-lg-4 col-md-6 d-flex flex-column align-items-center ">



                            <div className="card mb-3">

                                <div className="card-body">

                                    <div className="pt-4 pb-2">
                                        <h5 className="card-title text-center pb-0 fs-4">Order_Record</h5>
                                        {/* <h5 className="card-title text-center pb-0 fs-4">Add Pizza</h5> */}
                                        {/* <p className="text-center small">Add Pizza from Admin Side</p> */}
                                    </div>

                                    <form className="row g-3" >

                                        <div className="col-12">
                                            <label for="yourUsername" className="form-label">userID</label>
                                            <input type="text" name="itemName" className="form-control" id="yourUsername" required
                                                value={item.userID} onChange={handleInputs} />
                                        </div>
                                        <div className="col-12">
                                            <label for="yourUsername" className="form-label">shopID</label>
                                            <input type="text" name="description" className="form-control" id="yourUsername" required
                                                value={item.shopID} onChange={handleInputs}
                                            />
                                        </div>
                                        <div className="col-12">
                                            <label for="yourUsername" className="form-label">orderDate</label>
                                            <input type="text" name="description" className="form-control" id="yourUsername" required
                                                value={item.orderDate} onChange={handleInputs}
                                            />
                                        </div>
                                        <div className="col-12">
                                            <label for="yourUsername" className="form-label">totalAmount</label>
                                            <input type="text" name="description" className="form-control" id="yourUsername" required
                                                value={item.totalAmount} onChange={handleInputs}
                                            />
                                        </div>
                                        <div className="col-12">
                                            <label for="yourUsername" className="form-label">orderStatus</label>
                                            <input type="text" name="description" className="form-control" id="yourUsername" required
                                                value={item.orderStatus} onChange={handleInputs}
                                            />
                                        </div>
                                        <div className="col-12">
                                            <label for="yourUsername" className="form-label">paymentStatus</label>
                                            <input type="text" name="description" className="form-control" id="yourUsername" required
                                                value={item.paymentStatus} onChange={handleInputs}
                                            />
                                        </div>
                                        
                                      
                                       
                                        

                                       

                                        
                                    </form>

                                </div>
                            </div>


                        </div>

                        <div class="col-lg-8">

                            <div class="card justify-content-center">
                                <div class="card-body">


                                    <h5 class="card-title">Records of Pizzaa</h5>
                                    {/* <p>Add lightweight datatables to your project with using the <a href="https://github.com/fiduswriter/Simple-DataTables" target="_blank">Simple DataTables</a> library. Just add <code>.datatable</code> class name to any table you wish to conver to a datatable</p> */}

                                    <table class="table table-bordered table-sm datatable ">
                                        <thead>
                                            <tr>
                                                <th scope="col">Sr No.</th>
                                                <th scope="col">Name</th>
                                                <th scope="col">Description</th>
                                                <th scope="col">Small Price</th>
                                                <th scope="col">Medium Price</th>
                                                <th scope="col">Large Price</th>
                                                <th scope="col">Update</th>
                                                <th scope="col">Delete</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                          
                                            {items.map((item, index) => (
                                                <tr key={item._id}>
                                                    <th scope="row">{index+1}</th>
                                                    <td>{item.itemName}</td>
                                                    <td>{item.description}</td>
                                                    <td>Rs.{item.sizes.small.price}</td>
                                                    <td>Rs.{item.sizes.medium.price}</td>
                                                    <td>Rs.{item.sizes.large.price}</td>
                                                    <td>
                                                        <button className="btn btn-primary w-100" type="button" onClick={() => handleUpdate(item)}>Update</button>
                                                    </td>
                                                    <td>
                                                        <button className="btn btn-primary w-100" type="button" onClick={() => handleDelete(item._id)}>Delete</button>

                                                    </td>
                                                </tr>
                                            ))}

                                        </tbody>
                                    </table>

                                </div>
                            </div>

                        </div>

                    </div>
                </div>

            </section>
        </>
    )
}

export default Addpizza
