import React, { useState, useEffect } from 'react'
import { DataTable } from 'simple-datatables';
import './Orderrecord.css'
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify'; // Import toast from react-toastify
import 'react-toastify/dist/ReactToastify.css'; // Import the CSS for styling


const Orderrecord= () => {

  
    // const [itemsPerPage] = useState(); // Number of items to display per page (changed to 10)
    const [items, setItems] = useState([]);
    
    const [item, setItem] = useState({
        userID: "",
        shopID: "",
        orderDate: "",
        totalAmount: "",
        orderStatus: "",
        paymentStatus: ""
    });

    const navigate = useNavigate();


    useEffect(() => {
        // Fetch orders data from your API and set it in the 'items' state
        fetch("/getorder", {
            method: "GET",
            headers: {
                Accept: "application/json",
            },
        })
        .then((response) => response.json())
        .then((data) => {
            if (data.error) {
                toast.error(data.error);
            } else {
                setItems(data);
            }
        })
        .catch((error) => {
            console.error("Error fetching items:", error);
            toast.error('Error fetching items');
        });
    }, []);


    // Function to handle form input changes
    const handleInputs = (e) => {
        const { name, value } = e.target;
        setItem({ ...item, [name]: value });
    };

    // Function to handle price input changes for different sizes
    const handlePriceInputs = (e, size) => {
        const { name, value } = e.target;
        setItem({
            ...item,
            sizes: {
                ...item.sizes,
                [size]: {
                    ...item.sizes[size],
                    [name]: value
                }
            }
        });
    };







    // Function to handle form submission for adding a new item
    const postData = async (e) => {
        e.preventDefault();

        const { userID, shopID, orderDate, totalAmount, orderStatus, paymentStatus } = item;

        const response = await fetch("/addItem", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                Accept: "application/json",
            },
            body: JSON.stringify({
                userID,
                shopID,
                orderDate,
                totalAmount,
                orderStatus,
                paymentStatus
            }),
        });

        const res = await response.json();

        if (response.status === 201 || res.message === 'Item added successfully') {
            console.log("Item added successfully");
            toast.success("Item added successfully");

            setItem({
                userID: "",
                shopID: "",
                orderDate: "",
                totalAmount: "",
                orderStatus: "",
                paymentStatus: "",
                
                
            
            });
        } else {
            console.log("Failed to add item");
            toast.error("Failed to add item");
        }
    };


  
    useEffect(() => {
        const datatables = document.querySelectorAll('.datatable');

        datatables.forEach(datatable => {
            new DataTable(datatable);
        });
    }, []);
    return (
        <>
           <section className="section order-record min-vh-100 d-flex flex-column align-items-center py-4" id="order-record">
                <div className="container">
                    <div className="row">
                        <div className="col-lg-12">
                            <div className="card justify-content-center">
                                <div className="card-body">
                                    <h5 className="card-title">Order Records</h5>
                                    <table className="table table-bordered table-sm datatable">
                                        <thead>
                                            <tr>
                                                <th scope="col">User ID</th>
                                                <th scope="col">Shop ID</th>
                                                <th scope="col">Order Date</th>
                                                <th scope="col">Total Amount</th>
                                                <th scope="col">Order Status</th>
                                                <th scope="col">Payment Status</th>
                                                <th scope="col">Update</th>
                                                <th scope="col">Delete</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {Array.isArray(items) && items.length === 0 ? (
                                                <tr>
                                                    <td colSpan="8">No items found</td>
                                                </tr>
                                            ) : (
                                                items.map((item, index) => (
                                                    <tr key={item._id}>
                                                        <td>{item.userID}</td>
                                                        <td>{item.shopID}</td>
                                                        <td>{item.orderDate}</td>
                                                        <td>{item.totalAmount}</td>
                                                        <td>{item.orderStatus}</td>
                                                        <td>{item.paymentStatus}</td>
                                                       
                                                    </tr>
                                                ))
                                            )}
                                        </tbody>
                                    </table>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </>
    )
}

export default Orderrecord
