import React, { useEffect, useState } from 'react';
import './Menu.css';
import { useNavigate } from 'react-router-dom';
import PizzaCard from './Pizzacard'; // Import the PizzaCard component

const Menus = () => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState([]);

  const callMenuPage = async () => {
    try {
      const response = await fetch("/pizza", {
        method: "GET",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json"
        },
        credentials: "include"
      });

      const res = await response.json();
      console.log(res);
      setUserData(res);

      if (!response.ok) {
        const error = new Error(res.error);
        throw error;
      }
    } catch (err) {
      console.log(err);
      //   navigate("/login");
    }
  }

  useEffect(() => {
    callMenuPage();
  }, []);

  return (
    <>
      <section className="section">
        <div className="container">
          <div className="section-header">
            <h2>Our Menu</h2>
            <p>Check Our <span>Cheesy Pizza Menu</span></p>
          </div>

          <div className="row">
            {userData.map((menuItem) => (
              <PizzaCard key={menuItem.id} pizza={menuItem} />
            ))}
          </div>
        </div>
      </section>
    </>
  )
}

export default Menus;
