import React, { useState, useEffect } from 'react';
import { DataTable } from 'simple-datatables';
import './Orderrecord.css'; // Import your CSS file for styling
import { toast } from 'react-toastify'; // Import toast from react-toastify
import 'react-toastify/dist/ReactToastify.css'; // Import the CSS for styling

const Orderrecord = () => {
    const [orders, setOrders] = useState([]);
    const [updateMode, setUpdateMode] = useState(false);
    const [orderIdToUpdate, setOrderIdToUpdate] = useState('');
    const [order, setOrder] = useState({
        userID: '',
        shopID: '',
        orderDate: '',
        totalAmount: '',
        orderStatus: '',
        paymentStatus: '',
    });

    useEffect(() => {
        // Fetch orders data from your API and set it in the 'orders' state
        // You can use a similar approach as in your 'Addpizza.js' component
        fetch("/getorder", {
            method: "GET",
            headers: {
                Accept: "application/json",
            },
        })
            .then((response) => response.json())
            .then((data) => {
                setOrders(data);
            })
            .catch((error) => {
                console.error("Error fetching orders:", error);
            });
    }, []);

    // Define your functions for handling updates, deletions, etc.
    const handleUpdate = (order) => {
        // Set the order details in the form for editing
        setUpdateMode(true);
        setOrderIdToUpdate(order._id);
        setOrder({
            userID: order.userID,
            shopID: order.shopID,
            orderDate: order.orderDate,
            totalAmount: order.totalAmount,
            orderStatus: order.orderStatus,
            paymentStatus: order.paymentStatus,
        });
    };

    const handleCancelUpdate = () => {
        setUpdateMode(false);
        setOrderIdToUpdate('');
        setOrder({
            userID: '',
            shopID: '',
            orderDate: '',
            totalAmount: '',
            orderStatus: '',
            paymentStatus: '',
        });
    };

    const handleUpdateOrder = async () => {
        // Implement the logic for updating an order
        // Make a PUT request to update the order based on 'orderIdToUpdate'
        // Handle success and error cases accordingly

        // Example:
        try {
            const response = await fetch(`/updateOrder/${orderIdToUpdate}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    Accept: 'application/json',
                },
                body: JSON.stringify(order),
            });

            if (response.status === 200) {
                // Order updated successfully
                console.log('Order updated successfully');
                toast.success('Order updated successfully');

                // Refresh the orders data
                // You can refetch orders here or update the state accordingly
                // Example: fetchOrders();

                // Reset the state and exit update mode
                handleCancelUpdate();
            } else {
                // Handle update error
                console.log('Failed to update order');
                toast.error('Failed to update order');
            }
        } catch (error) {
            console.error('Error updating order:', error);
            // Handle any other errors that may occur during updating
        }
    };

    const handleDelete = async (orderId) => {
        // Implement the logic for deleting an order
        // Make a DELETE request to delete the order based on 'orderId'
        // Handle success and error cases accordingly

        // Example:
        try {
            const response = await fetch(`/deleteOrder/${orderId}`, {
                method: 'DELETE',
            });

            if (response.status === 200) {
                // Order deleted successfully
                console.log('Order deleted successfully');
                toast.success('Order deleted successfully');

                // Refresh the orders data
                // You can refetch orders here or update the state accordingly
                // Example: fetchOrders();
            } else {
                // Handle delete error
                console.log('Failed to delete order');
                toast.error('Failed to delete order');
            }
        } catch (error) {
            console.error('Error deleting order:', error);
            // Handle any other errors that may occur during deletion
        }
    };

    // Megh
    useEffect(() => {
        const datatables = document.querySelectorAll('.datatable');

        datatables.forEach(datatable => {
            new DataTable(datatable);
        });
    }, []);

    return (
        <>
            <section className="section order-record min-vh-100 d-flex flex-column align-items-center py-4" id="order-record">
                <div className="container">
                    <div className="row">
                        <div className="col-lg-12">
                            <div className="card justify-content-center">
                                <div className="card-body">
                                    <h5 className="card-title">Order Records</h5>
                                    <table className="table table-bordered table-sm datatable">
                                        <thead>
                                            <tr>
                                                <th scope="col">User ID</th>
                                                <th scope="col">Shop ID</th>
                                                <th scope="col">Order Date</th>
                                                <th scope="col">Total Amount</th>
                                                <th scope="col">Order Status</th>
                                                <th scope="col">Payment Status</th>
                                                <th scope="col">Update</th>
                                                <th scope="col">Delete</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {Array.isArray(orders) && orders.map((order, index) => (
                                                <tr key={order._id}>
                                                    <td>{order.userID}</td>
                                                    <td>{order.shopID}</td>
                                                    <td>{order.orderDate}</td>
                                                    <td>{order.totalAmount}</td>
                                                    <td>{order.orderStatus}</td>
                                                    <td>{order.paymentStatus}</td>
                                                    <td>
                                                        <button className="btn btn-primary w-100" onClick={() => handleUpdate(order)}>Update</button>
                                                    </td>
                                                    <td>
                                                        <button className="btn btn-primary w-100" onClick={() => handleDelete(order._id)}>Delete</button>
                                                    </td>
                                                </tr>
                                            ))}
                                        </tbody>
                                    </table>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </>
    );
};

export default Orderrecord;
